mapper.select:
select id               as user_id,
	   nickname,
	   avatar,
	   high_score,
	   @row := @row + 1 as sequence
from (select @row := 0) as rowtemptable,
	 (select *
	  from `user`
	  order by high_score desc
	  limit 10) as playerinfo;

select id               as user_id,
	   nickname,
	   avatar,
	   high_score,
	   @row := @row + 1 as sequence
from (select @row := 0) as temp,
	 (select *
	  from `user`
	  order by high_score desc) as playertable
where id = #{userId}
