let loginUserNameInput=document.querySelector(".formbody .userName input"),loginPwdInput=document.querySelector(".formbody .pwd input"),loginRequestTip=document.querySelector(".requesting");document.querySelector(".formbody .ok").addEventListener("click",()=>{let data={userName:loginUserNameInput.value,pwd:loginPwdInput.value},dataString=JSON.stringify(data);loginRequestTip.style.display="flex",fetch("/miyakogame/api/login",{method:"POST",body:dataString,headers:{"content-type":"application/json"}}).then(response=>response.json()).then(result=>{result.success?(showTipFrame("登录成功！3s后跳转至游戏界面。",null,".tip-true"),setTimeout(()=>{window.location.pathname="/miyakogame"},3e3)):showTipFrame("登录失败！原因："+result.message,null,".tip-false"),loginRequestTip.style.display="none"})});