let weaponList=[];function Weapon(name,price,texture,shootingSound,flyingMethod,hitTrigger){this.name=name,this.price=price,this.texture=texture,this.shootingSound=shootingSound,this.dom=document.createElement("img"),this.dom.src=texture,this.dom.style.position="absolute",this.flyingMethod=flyingMethod,this.hitTrigger=hitTrigger,this.createBullet=()=>{gameBackground.appendChild(this.dom),shootingSound.play()}}let defaultWeapon=new Weapon("常规鬼火",0,"/img/bullets/bullet.png",document.querySelector(".fireAudio"),()=>{let x=defaultWeapon.dom.offsetLeft;x+=8,defaultWeapon.dom.style.left=x+"px"},enemy=>{document.querySelector(".scoreAduio").play(),enemyFadeEffect(enemy),defaultWeapon.dom.remove()});weaponList.push(defaultWeapon);